            imagem_logo.Top = ShapePosition.Top;
            imagem_logo.Left = ShapePosition.Left;
            imagem_logo.WrapFormat.Style = WrapStyle.Through;
            imagem_logo.Width = 100;
            imagem_logo.Height = 50;

            // Logo da prefeitura
