            frame.Height = "3.0cm";
            frame.Width = "20cm";
            frame.Left = ShapePosition.Left;
            frame.RelativeHorizontal = RelativeHorizontal.Margin;
            frame.Top = "5.0cm";
            frame.RelativeVertical = RelativeVertical.Page;

            // título
